import LoginForm from './LoginForm';
import React from 'react';


class LoginPage extends React.Component {
  constructor(props) {

    super(props);
    //init state
    this.state = {
      errors: {
        summary: 'Summary Error!',
        email: 'Email Error!',
        password: 'Password Error!'
      },
      user: {
        email: '',
        password: ''
      }
    };

  }

  processForm(event) {
    // Prevent default action. In this case, default action is the form submission event.
    event.preventDefault();
    const email = this.state.user.email;
    const password = this.state.user.password;
    console.log('email:', email);
    console.log('password:', password);

    //Post login data.
  }

  // not change a user but change values of the fields
  changeUser(event) {
    const field = event.target.name;
    const user = this.state.user;
    user[field] = event.target.value;
    this.setState({user});
  }

  // render html
  render() {
    return (
      <LoginForm
        onSubmit={(event) => this.processForm(event)}
        onChange={(event) => this.changeUser(event)}
        errors={this.state.errors}
      />
    );
  }
}


export default LoginPage;
