import './LoginForm.css';

// need to install first
import PropTypes from 'prop-types';
import React from 'react';

// side note
// short hand for declaring a function
// curly braces in param denotes object literal

const LoginForm = ({
  onSubmit,
  onChange,
  errors,
}) => (
  <div className="container">
    <div className="card-panel login-panel">
      <form className="col s12" action="/" onSubmit={onSubmit}>
        <h4 className="center-align">Login</h4>
        {errors.summary && <div className="row"><p className="error-message">{errors.summary}</p></div>}
        <div className="row">
          <div className="input-field col s12">
            <input className="validate" id="email" type="email" name="email"
                   onChange={onChange}/>
            <label htmlFor='email'>Email</label>
          </div>
        </div>
        {errors.email && <div className="row"><p className="error-message">
          {errors.email}</p></div>}
        <div className="row">
          <div className="input-field col s12">
            <input className="validate" id="password" type="password" name
              ="password" onChange={onChange}/>
            <label htmlFor='password'>Password</label>
          </div>
        </div>
        {errors.password && <div className="row"><p className="error-message">{errors.password}</p></div>}
        <div className="row right-align">
          <input type="submit" className="waves-effect waves-light btn indigo lighten-1" value='Log in'/>
        </div>
        <div className="row">
          <p className="right-align"> New to Tap News? <a href="/signup">Si
            gn Up</a></p>
        </div>
      </form>
    </div>
  </div>
);


LoginForm.propTypes = {
  onSubmit: PropTypes.func.isRequired,
  onChange: PropTypes.func.isRequired,
  error: PropTypes.func.isRequired,
};

// export {LoginForm};
export default LoginForm;
